  methods: {
    createTuser: function createTuser() {
      var _this = this;

      axios.post('/api/v1/tusers', this.tuser).then(function (response) {
        var e = response.data;

        _this.$router.push({
          name: 'TuserDetailPage',
          params: {
            id: e.id
          }
        });
      })["catch"](function (error) {
        console.error(error);

